import React, { Component } from 'react'
import Info from './Info/Info'
import  './Body.css'
import Axios from 'axios';


class Body extends Component{
    state={
        covidvalue:{
            "Tested_positive":36456,
        "Tested_negative":621654,
        "Tested_total":658110,
        "In_isolation":16019,
        "Quarantined":8939,
        "Tested_rdt":312402,
        "Pending_result":0,
        "Recovered":20242,
        "Deaths":195
    }
    };

    componentDidMount(){
        let ax=[]
        Axios.get("https://nepalcorona.info/api/v1/data/nepal")
        .then(response=>{
            Object.keys(response.data).map((key, i) => (

                ax[i] =<option key={i}>{key.replace("_"," ")}</option>
               )
             )
        })
    }

    render(){
        let printinfo=[]
        Object.keys(this.state.covidvalue).map((key, i) => (
           printinfo[i]=<Info key={i} type="info" number={this.state.covidvalue[key]} title={key.replace("_"," ")}/>
          )
        )
       return(
        <div className="body">
            <div className="row">
            {printinfo}

            </div>
                <select>
                    {ax}
                </select>
        </div>
        )
    }
}

export default Body
