import React from 'react'


const Info = (props) => {
    let classType=""
    let addedstring=null
    if(props.type==="info")
    {
        classType="card text-white bg-info mb-4"
    }
    else if (props.type==="success")
    {
        classType="card text-white bg-success mb-4"

    }
    else if(props.type==="danger")
    {
        classType="card text-white bg-danger mb-4"

    }
    else if (props.type="yellow")
    {
        classType="card text-white bg-warning mb-3"        
    }
    return (
        <div className="col">
            <div className={classType}>
            <div className="card-header">{props.number}</div>
             <div className="card-body">
    <h5 className="card-title">{props.title}</h5>
         </div>
        </div>
        </div>
    )
}

export default Info
