import React, { Component } from 'react'
import Info from './Info/Info'
import  './Body.css'
import Axios from 'axios';


class Body extends Component{
    state={
        covidvalue:{
            "Tested_positive":36456,
        "Tested_negative":621654,
        "Tested_total":658110,
        "In_isolation":16019,
        "Quarantined":8939,
        "Tested_rdt":312402,
        "Pending_result":0,
        "Recovered":20242,
        "Deaths":195
    },
    options:{}
    };

    componentDidMount(){
        Axios.get("https://nepalcorona.info/api/v1/data/nepal")
        .then(response=>{
            this.setState({

                options:response.data
            })
        })
    }

    render(){
        let printinfo=[]
        let options=[]
        Object.keys(this.state.covidvalue).map((key, i) => (
           printinfo[i]=<Info key={i} type="success" number={this.state.covidvalue[key]} title={key.replace("_"," ")}/>
          )
        )
        
        Object.keys(this.state.options).map((key, i) => (
           options[i]=<option key={i}>{key.replace("_"," ")}</option>
            )
         )
       return(
        <div className="body">
            <select style={{'width':'100%','height':'50px'}}>
                    <option disabled selected>--Please select the required data you want to show---</option>
                    {options}
            </select>
            <div class="result">

                <hr style={{'width':'100%','height':'50px'}}/>
            {printinfo}

            </div>

                
        </div>
        )
    }
}

export default Body
