import React, { Component } from 'react'
import Info from './Info/Info'
import  './Body.css'
import Axios from 'axios';


class Body extends Component{
    state={
        covidvalue:[]
    };

    componentDidMount(){
        Axios.get("https://nepalcorona.info/api/v1/data/nepal")
        .then(response=>{
            this.setState({
                covidvalue:response.data
            })
        })
    }

    render(){
        let printinfo=[]
        Object.keys(this.state.covidvalue).map((key, i) => (
           printinfo[i]=<Info key={i} type="info" number={this.state.covidvalue[key]} title={key.replace("_"," ")}/>
          )
        )
       return(
        <div className="body">
                {printinfo}
        </div>
        )
    }
}

export default Body
