import React from 'react'


const Info = (props) => {
    let classType=""
    if(props.type==="info")
    {
        classType="card text-white bg-info mb-3"
    }
    else if (props.type==="sucess")
    {
        classType="card text-white bg-success mb-3"

    }
    else if(props.type==="danger")
    {
        classType="card text-white bg-danger mb-3"

    }
    return (
        <div className="col">
            <div className={classType}>
            <div className="card-header">Header</div>
             <div className="card-body">
         <h5 className="card-title">{props.type} card title</h5>
             <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
         </div>
        </div>
        </div>
    )
}

export default Info
